package com.wipro;

public class while_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("print Odd numbers from 1 to 10");
		int x=1;
		while(x<=10)
		{
			if(x%2!=0)
			{
				System.out.println(x);
				
			}
			x++;
		}

	}

}
